func ControlCommand(string command, object target, int x, int y, object target2, int data, object command_object)
{
  // Ein Kommando mit Zielobjekt
  if (target)
    Message("Befehl: %s Ziel: %s Koordinaten: %d/%d", command, target->GetName(), x, y);
  // Ein Kommando ohne Zielobjekt
  else
    Message("Befehl: %s Koordinaten: %d/%d", command, x, y);
  // Kommando nicht abfangen
  return(0);
}

public func ContextConstruction(object caller)
{
  [Bauauftrag|Image=CXCN|Condition=HasKnowledge]
  SetCommand(this, "Construct");
  return true;
}
