part.code:
static foo;
func f() {
	var foo = 3;
}

func f() {
	var i;
	var i;
}

func f(a) {
	i = 0;
	if (a) {
		var i = 1;
	}
	return i;
}

func f(a) {
	return Par(a);
}
// Better:
func g(a, ...) {
	return Par(a);
}

