part.code:
static iObjectCount;
local  iTimeToLive;

protected func Initialize()
{
  iObjectCount++;
  iTimeToLive = 100;
}

protected func Destruction()
{
  iObjectCount--;
}

protected func Timer()
{
  if(!--iTimeToLive)
  {
    RemoveObject();
    return();
  }

  var obj = FindObject(Clonk, 0, 0, -1, -1);

  if(ObjectDistance(obj) < 20)
    DoDamage(-10, obj);

  return();
}
