example.code:
#strict 2
#appendto Clonk

func Initialize() {
  AddEffect("VariateHue",this(),200,1,this());
  return _inherited();
}

func FxVariateHueTimer() {
  var rgb, hsl=RGB2HSL(GetColorDw());
  var hue, sat, light;
  SplitRGBaValue(hsl, hue, sat, light);
  hue+=2;
  if(hue>255) hue=0;
  rgb=HSL2RGB(RGB(hue,sat,light));
  SetColorDw(rgb);
}
