example.code:
static g_pCursor, g_dwPrevBlitMode, g_dwPrevMod;

protected func Script100()
  {
  // Ausgewählten Clonk des ersten Spielers ermitteln
  g_pCursor = GetCursor();
  // Vorherige Werte speichern
  g_dwPrevBlitMode = g_pCursor->GetObjectBlitMode();
  g_dwPrevMod = g_pCursor->GetClrModulation();
  // Nicht-ColorByOwner-Teile des Clonks leuchtend grün färben
  g_pCursor->SetObjectBlitMode(6);
  g_pCursor->SetClrModulation(RGB(100, 255, 110));
  // Nachricht
  g_pCursor->Message("Schau her, ich kann leuchten!");
  return true;
  }

protected func Script200()
  {
  // Farbe zurücksetzen
  g_pCursor->SetObjectBlitMode(g_dwPrevBlitMode);
  g_pCursor->SetClrModulation(g_dwPrevMod);
  // Nachricht
  g_pCursor->Message("Schon vorbei :/");
  return 1;
  }
