example.code:
protected func Activate(eater)
  {
  [$DescEatPill$]
  eater->SetPhysical("Magic", 1000000, PHYS_Temporary);
  while(eater->DoMagicEnergy(1)) ;
  RemoveObject();
  }

protected func FxJumpBoostStart(object trg, int num, int temporary)
  {
  trg->SetPhysical("Jump", trg->GetPhysical("Jump", PHYS_Current) + 10000, PHYS_StackTemporary);
  return(FX_OK);
  }

protected func FxJumpBoostStop(object trg, int num, int reason, bool tmp)
  {
  trg->ResetPhysical("Jump");
  return(FX_OK);
  }
