global func SetObjectPicture(idSrcDef, pObj)
  {
  pObj->SetPicture(GetDefCoreVal("Picture", "DefCore", idSrcDef, 0),
             GetDefCoreVal("Picture", "DefCore", idSrcDef, 1),
             GetDefCoreVal("Picture", "DefCore", idSrcDef, 2),
             GetDefCoreVal("Picture", "DefCore", idSrcDef, 3));
  pObj->SetGraphics(0, idSrcDef);
  return(1);
  }
