example.code:
func SaveWealth()
{
  for(var i=0; i<GetPlayerCount(); i++)
    GetPlayer(GetPlayerByIndex(i))->SetExtraData("MySzen_Wealth", GetWealth(i));
}

func Initialize()
{
  for(var i=0; i<GetPlayerCount(); i++)
    SetWealth(i, GetPlayer(GetPlayerByIndex(i))->GetPlrExtraData("MySzen_Wealth"));
}
