example.code:
protected func InitializePlayer(int iPlr)
  {
  var iPlrID = GetPlayerID(iPlr), iColID = 1;
  SetScoreboardData(SBRD_Caption, iColID,       "{{RVLR}}",                iColID);
  SetScoreboardData(iPlrID,       iColID,       "0" ,                      0);
  SetScoreboardData(SBRD_Caption, SBRD_Caption, "Score",                   SBRD_Caption);
  SetScoreboardData(iPlrID,       SBRD_Caption, GetTaggedPlayerName(iPlr), iPlrID);
  SortScoreboard(iColID, true);
  Global(iPlr)=0;
  return(1);
  }

protected func RemovePlayer(int iPlr)
  {
  var iPlrID = GetPlayerID(iPlr), iColID = 1;
  SetScoreboardData(iPlrID, iColID, 0 ,0);
  }

public func AddScore(int iPlr)
  {
  var iPlrID = GetPlayerID(iPlr), iColID = 1, iScore = ++Global(iPlr);
  SetScoreboardData(iPlrID, iColID, Format("%d", iScore), iScore);
  SortScoreboard(iColID, true);
  DoScoreboardShow(1, iPlr+1);
  Schedule(Format("DoScoreboardShow(-1, %d)", iPlr+1), 200);
  }
